import random
def roll_dice(parameters):
    count = 1
    if 'count'  in parameters:
        count = int(parameters['count'])
    if count <= 0:
        return []
    sides = int(parameters['sides'])
    if sides <= 1:
        return []
    results = []
    for _ in range(count):
        results.append(1+int(sides*random.random()))
    return results
